// import HomeContainer from "./HomeContainer";
import PostCard from "./components/PostCard";
import Profile from "./Profile";
import { getPost } from './redux/actions/PostActions'
import { connect } from 'react-redux';
import { fetchUser } from './redux/actions/UserActions';
import Loader from './components/loader'



class Homepage extends React.Component {
  state = {
    userId: this.props.userid,
    name: this.props.user,
    showCommentField: false,
    comment: "",
    post_id: "",
    Picture: "",
    caption: "",
    likes: 0,
    page: "thePost"

  };

  theFetch = async way => {
    let url;
    way
      ? (url = `http://localhost:3000/${way}`)
      : (url = "http://localhost:3000/");
    const resp = await fetch(url);
    return await resp.json();
  };
  componentDidMount() {
    const { fetchUser, getPost } = this.props
    fetchUser(localStorage.token)
    getPost()

  }

   thePost =()=>{ return this.props.post.map(p => <PostCard post={p}/>)}




  render() {
    console.log("Home Page Props:", this.props)
    const { user, post } = this.props
    return (
      <>
      {!post.requested && <Loader/>}
      {this.thePost}
        {/* <HomeContainer
          history={this.props.history}
        /> */}
      </>
    );
  }
}
const mapStateToProps = state => {
  return {
    token: state.login.token,
    user: state.users,
    post: state.post.post,
  }
}



export default connect(mapStateToProps, { fetchUser, getPost  })(Homepage)
