import { connect } from 'react-redux';

export const TheUser = (id) =>{

   username =  this.props.users.find( u => u.id === id)
   return username

}
const mapStateToProps = state => {
    return{
        users: state.users.all
    }
}
export default connect(mapStateToProps)(TheUser)
