import React from "react";
import {
  Form,
  Spinner,
  Navbar,
  InputGroup,
  Button,
  FormControl
} from "react-bootstrap";
// Where I got the components from : https://react-bootstrap.github.io/components/alerts/

class Login extends React.Component {

  state ={
    username:'',
    password: ''
  }

  handleChage =(e)=>{
    this.setState({
      [e.target.name]: e.target.value
      }
      )
  }

  handleSubmit=(e)=>{
  e.preventDefault()
  fetch('http://localhost:3000/login',{
    method: 'POST',
    headers: {

    }
  })
  }

  render() {
    console.log(this.state)
    return (
      <div className="signindiv">
                    <Navbar className="login-nav">
              <Navbar.Brand href="#home">
              <h1 className="sign-in-header">Jays'taGram </h1>
              </Navbar.Brand>
              <Navbar.Toggle />
              <Navbar.Collapse className="justify-content-end">
                <Navbar.Text>
                  Signed in as: <a href="#login">Put Users Name here</a>
                </Navbar.Text>
              </Navbar.Collapse>
            </Navbar>

        <div><img src="https://www.jing.fm/clipimg/full/90-902599_camera-icons-transparent-background-ios-camera-logo-png.png" className="camera-logo"/></div>
        <Form className="signInForm" onSubmit={this.handleSubmit}>
          <Form.Group controlId="formGroupEmail">
            <Form.Label alt="username-field"></Form.Label>
            <Form.Control type="text" value={this.state.username} placeholder="Username"  name="username" onChange={this.handleChage}/>
          </Form.Group>
          <Form.Group controlId="formGroupPassword">
            <Form.Label alt="password-field"></Form.Label>
            <Form.Control type="password" name="password" value={this.state.password} onChange={this.handleChage} placeholder="Password" />
          </Form.Group>
          <Button variant="primary" type="submit">Login</Button>
        </Form>
        <Spinner animation="grow" variant="info" />
      </div>
    );
  }
}

export default Login;
