           const Friend2 =  users.find((user => { user.id  === follow.followee_id || follow.follower_id;} )).username

           friendships.push({ Friend1 : Friend2})
           return friendships
        })

    }
    render() {
        this.allFriends()
        return (
            <div>

            </div>
        )
    }
}
const mapStateToProps = state => {
    return {
        follows: state.follows,
        users: state.users.all
    }
}

export default connect(mapStateToProps, { getFollows, fetchAllUsers})(Follows)
