import React from "react";
import "./components/HomeContainer.css";
import Postcard from "./components/PostCard";
import NewPostCard from "./components/NewPostCard";

import { Card, Form, Navbar, Button, NavbarBrand, Nav } from "react-bootstrap";

class HomeContainer extends React.Component {
  state = {
    id: "",
    comment: "",
    post_id: "",
    picture: "",
    caption: "",
    likes: 0,
    newPost: true
  };

  info = () => {
    console.log(this.props);
    return this.props;
  };
  // POST COMMENT FETCH POST
  postComment = (id, content, fId) => {
    return fetch("http://localhost:3000/comments", {
      method: "POST",
      headers: {
        "Content-Type": "Application/json",
        Accept: "application/json"
      },
      body: JSON.stringify({
        post_id: id,
        content: content,
        followee_id: fId
      })
    });
  };

  // HANDLER FOR COMMENTS
  handleComment = e => {
    // console.log(e.target.value);
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  thePost = props => {

    this.props.fposts.map(post => (
      <Postcard
        post={post}
        id={post.id}
        submitComment={() => this.submitComment(post.id, post.userId)}
        handleComment={this.handleComment}
      />
    ));
  };
  // SUBMIT THE COMMENT /FETCH POST
  handleNewPostClick = () => {
    this.setState({ newPost: !this.state.newPost });
    return this.state
    console.log(this.props)
  };

  theNewPostCard = props => {
    return (
      <NewPostCard
        handleNewPost={this.handleNewPost}
        submitPost={() => this.submitNewPost(this.state.id)}
        state={this.state}
        userId={this.state.id}
      />
    );
  };

  pageToRender = () => {
    // debugger;
    return this.handleNewPostClick ? this.thePost(this.props) : this.theNewPostCard(this.props);
  };
  // this.state.cameraClick ? NewPost() :

  submitComment = (postId, userId) => {
    console.log(
      "Post id",
      postId,
      "User is:",
      userId,
      "comment: ",
      this.state.comment
    );
    this.postComment(postId, this.state.comment, userId)
      .then(resp => resp.json())
      .then(console.log);
  };

  submitNewPost = id => {
    console.log(id);
    const postUlr = "http://localhost:3000/posts";
    console.log(
      "The state we are submitting",
      this.state.picture,
      this.state.caption,
      id
    );
    fetch(postUlr, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json"
      },
      body: JSON.stringify({
        user_id: id,
        picture: this.state.picture,
        caption: this.state.caption,
        likes: 0
      })
    })
      .then(res => res.json())
      .then(data => {
        this.setState({
          newPost: false
        });
      });
    // this.handleNewPostClick();
  };

  handleNewPost = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  render() {
    // console.log("New Post Status", this.state.newPost);
    // const { fposts, user, userId } = this.props;

    // const thePost = fposts.map(post => (
    //   <Postcard
    //     post={post}
    //     id={post.id}
    //     submitComment={() => this.submitComment(post.id, userId)}
    //     handleComment={this.handleComment}
    //   />
    // ));

    // console.log("Home Container Props:", this.props);
    return (
      <div className="Home-Container">
        <div className="Home-Content">
          <div className="signindiv">
            <Navbar>
              <Navbar.Brand>
                <h3>Jays'taGram </h3>
                <Nav.Item>
                  <Button
                    variant=""
                    className="margin-auto"
                    onClick={() => this.handleNewPostClick()}
                  >
                    <span id={this.state.id}>ðŸ“¸</span>
                  </Button>
                </Nav.Item>
              </Navbar.Brand>
              <Navbar.Toggle />
              <Navbar.Collapse className="justify-content-end">
                <Navbar.Text>
                  <Navbar.Brand href="/profile">
                    Welcome Back {this.props.user}
                  </Navbar.Brand>
                </Navbar.Text>
              </Navbar.Collapse>
            </Navbar>
          </div>

          <div className="Home-main"></div>
          <h2 style={{ color: "black" }}>We've missed YOU...</h2>
          {this.pageToRender()}

          {/* {this.handleNewPostClick ? (
            thePost
          ) : (
            <NewPostCard
              handleNewPost={this.handleNewPost}
              submitPost={() => this.submitNewPost(userId)}
              state={this.state}
              userId={userId}
            />
          )} */}
        </div>
        <div className="Home-footer">Copyright &copy; 2019 Jaystagram</div>
      </div>
    );
  }
}

export default HomeContainer;
