import {SUBMITTED_COMMENT,ADD_COMMENT, EDIT_COMMENT, DELETE_COMMENT} from './types';
import axios from 'axios'


export const addComment =(info)=> dispatch =>{
    dispatch( { type: SUBMITTED_COMMENT} )
  return  axios.post('http://localhost:3000/comments/', info).then(data=>{
      console.log("From Commne",data)
  })


}
