import { USER_REQUEST, USER_SUCCESS, USER_FAILURE, CREATE_USER, UPDATE_USER, DELETE_USER } from '../actions/types';
import axios from 'axios';
import { database } from 'firebase';

export const fetchUser = (token) => async dispatch => {
    dispatch({ type: USER_REQUEST })
    debugger
    const config = {
        headers: {
            "Content-Type": "Application/json",
            Authorization: token
        }
    }
    const user = await axios.get('http://localhost:3000/profile', config);
    !!user ? dispatch({ type: USER_SUCCESS, payload: user }) : dispatch({ type: USER_FAILURE, payload: user })
    return console.log("From fetch User in UsersActions", user);

    // (database.username , data.id)
}
