import React from 'react';
import '../css/ProfilePostCard.css';
// import '../css/Profile.css';

export default function ProfilePostCard(props) {
    const {post, user } = props
    return (

        <div className="profile-post-card-div" id={`${post.id}`} onClick={(e) => console.log(e.target.id)}>
        <div id={post.id} className="profile-post-card">
          <div className="profile-card-header">

            {/* REPLACE WITH THE IMAGE ASSOCIATED WITH NAME
          <img
            className="post-thumbnail"
            src={require("../img/jack.jpg")}
          />  */}

            {/* {areFriends(post.user_id)} */}

            <span className="proname-span-style" onClick={(e) => { console.log(e) }} >{user}</span>
          </div>

          <div className="proimg-div">
            <img className="proimage" src="" />
          </div>



          <div className="procomments-div" id={post.id} onClick={(e) => console(e)}>
            <span id="proon-heart" onClick={() => console.log("post.id, post.likes")}>‚ù§Ô∏è</span>
              :
              <span id="prooff-heart" onClick={() => console.log("post.id, post.likes")}>‚ô°</span>
            <div id='procomments-header'>
              <span id={post.id} className="propen" onClick={(e) => console.log("activeComment",e)}
              >{"props.commentFieldStatus ? üí¨  :  üñã "}</span>
              <span className="prolikes" >Likes: {post.likes}</span>

            </div>
            <br />
            <div className='proul-style'>

              <p className='propost-caption'><span id="proname-cap"><strong>{' ${whichUser()}'} : </strong></span> {'props.editCapStatus ? editCapInput(post.caption) : post.caption'} {'props.editCapStatus ? <span id="prosubmit-cap-edit" onClick={() => console.log("props.submitCapEdit",post.id)}>  ‚¨ÜÔ∏è </span> : <span id="proedit-caption" onClick={() => console.log("props.getCapEditField",post.id)}>üñã</span>'} </p>

              {/* {comment()} */}
            </div>
            {props.commentFieldStatus ? <input
              size="sm"
              type="text"
              name="comment"
              value={"props.comment"}
              onChange={"props.handleComment"}
              placeholder="comment"
              className="procomment-input"
            /> : <><br /></>}
            <br />
            {'props.status && <Loader />'}
            <br />
            {'props.commentLen > 0 && !props.editCapStatus &&' }
            <span onClick={props.submitComment}
              id="propost-span">
              ‚¨ÜÔ∏è
        </span>


          </div>

        </div>

      </div>
    )
}
