  //       method: 'POST',
  //       headers:{
  //         'Content-Type': 'Application/json'
  //       },
  //       body :JSON.stringify({
  //           id,
  //           content,
  //           fId,
  //       })

  //   })
  // }

  render() {
    // console.log(this.state)
    return (
      <Fragment>
      <Router>
       <Switch>
        <HomeContainer
          // fposts={this.state.follooweePosts}
          // user={this.state.currentUser}
          // userid={this.state.id}
        />
      </Switch>
      </Router>
      </Fragment>
    );
  }
}
export default Homepage;
